DROP TABLE IF EXISTS `ppy7i_user_notes`;
CREATE TABLE `ppy7i_user_notes` (   `id` int(10) unsigned NOT NULL AUTO_INCREMENT,   `user_id` int(10) unsigned NOT NULL DEFAULT '0',   `catid` int(10) unsigned NOT NULL DEFAULT '0',   `subject` varchar(100) NOT NULL DEFAULT '',   `body` text NOT NULL,   `state` tinyint(3) NOT NULL DEFAULT '0',   `checked_out` int(10) unsigned NOT NULL DEFAULT '0',   `checked_out_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',   `created_user_id` int(10) unsigned NOT NULL DEFAULT '0',   `created_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',   `modified_user_id` int(10) unsigned NOT NULL,   `modified_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',   `review_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',   `publish_up` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',   `publish_down` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',   PRIMARY KEY (`id`),   KEY `idx_user_id` (`user_id`),   KEY `idx_category_id` (`catid`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `ppy7i_user_profiles`;
CREATE TABLE `ppy7i_user_profiles` (   `user_id` int(11) NOT NULL,   `profile_key` varchar(100) NOT NULL,   `profile_value` varchar(255) NOT NULL,   `ordering` int(11) NOT NULL DEFAULT '0',   UNIQUE KEY `idx_user_id_profile_key` (`user_id`,`profile_key`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Simple user profile storage table';
DROP TABLE IF EXISTS `ppy7i_user_usergroup_map`;
CREATE TABLE `ppy7i_user_usergroup_map` (   `user_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'Foreign Key to #__users.id',   `group_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'Foreign Key to #__usergroups.id',   PRIMARY KEY (`user_id`,`group_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
